class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      name:"",
      age:0
    }
    this.change = this.change.bind(this)
    this.write = this.write.bind(this)
    this.read = this.read.bind(this)
  }

  change(key,e){
    this.setState({
      [key]:e.target.value
    });
  }

  async write(){
    let data = {...this.state};
    console.log(data);
    let res = await axios.post(`${server}/write/`,data);
    console.log(res);
  }

  async read(){
    let res = await axios.get(`${server}/read/`);
    console.log(res);
  }

  render() {
    return (
      <div className="App">
        <div className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h2>欢迎来到菜鸟教程</h2>
        </div>
        <p className="App-intro">
          你可以在 <code>src/App.js</code> 文件中修改。
        </p>

        <input onchange={(e) => (this.change('name',e))} />
        <br/>
        <input onchange={(e) => (this.change('age',e))} />
        <br/>
        <button onClick={this.write}>write</button>
        <button onClick={this.read}>read</button>
      </div>
    );
  }
}

export default App;
