class App extends Component{
  constructor(props){
    super(props);
    this.state = {
      classroom_no:"",
      capacity:0
    }
    this.change = this.change.bind(this)
    this.write = this.write.bind(this)
    this.read = this.read.bind(this)
  }

  change(key,e){
    this.setState({
      [key]:e.target.value
    });
  }

  async write(){
    let data = {...this.state};
    console.log(data);
    let res = await axios.post(`${server}/write/`,data);
    console.log(res);
  }

  async read(){
    let params = {
      classroom_no:'Z3309'
    }

    let res = await axios.get(`${server}/read/`,{params});
    console.log(res);
  }

  render() {
    return (
      <div className="App">
        <input onChange={(e) => (this.change('classroom_no',e))} />
        <br/>
        <input onChange={(e) => (this.change('capacity',e))} />
        <br/>
        <button onClick={this.write}>write</button>
        <button onClick={this.read}>read</button>
      </div>
    );
  }
}

export default App;
